  private void callGetCoinFromGrpcServer() {
    int i = new Random().nextInt(15);
    try {
      log.info("fetching {}", grpcClient.getCoinDetails(coins[i]));
    } catch (Exception e) {
      log.info("Error in fetching the coin details for: "+coins[i]);
    }
  }

  private void callGetMultipleCoinsFromGrpcServer() {
    List<String> symbols = Arrays.asList(coins);
    try {
      Iterator coinsIterator = grpcClient.getMultipleCoinDetails(symbols);
      while(coinsIterator.hasNext()) {
        log.info("Fetching the data for all coins: {}", coinsIterator.next());
      }
    } catch (Exception e) {
      e.printStackTrace();
      log.info("Error in fetching the coin details for: "+symbols);
    }
  }

